mapper.select:
select * from( select fundno, fundname, proposal, homepage, cursum, goalsum, participant, startdate, duedate, content, hits, category, source, progress, ceil(rownum/9) as page from (select fund_no fundno, fund_name fundname, proposal, homepage, cur_sum cursum, goal_sum goalsum, participant, to_char(start_date,'yyyy-mm-dd') as startdate, to_char(due_date,'yyyy-mm-dd') as duedate, content, hits, category, source, nullif(cur_sum,0)/goal_sum*100 as progress from gibu_fund
category = #{category}
and
fund_name like '%' || #{word} || '%'
order by fund_no desc
order by due_date desc
order by progress desc
order by progress asc
)) where page=#{pageNo}

select page from( select fundno, ceil(rownum/5) as page from( select fund_no as fundno, nullif(cur_sum,0)/goal_sum*100 as progress from gibu_fund
category = #{category}
and
fund_name like '%' || #{word} || '%'
order by fund_no desc
order by due_date desc
order by progress desc
order by progress asc
)) where page=#{pageNo}

mapper.update:
update gibu_fund set
fund_name=#{fundName},proposal=#{proposal},
homepage=#{homepage},goal_sum=#{goalSum}, start_date=#{startDate},
due_date=#{dueDate},content=#{content}, category=#{category},
source=#{source}
where fund_no=#{fundNo}

mapper.select:
select * from(
	select comment_no, fund_no, text,
	comment_time, member_id, name, ceil(rownum/5) as page from(
		select co.comment_no, co.fund_no, co.text, to_char(co.comment_time,'yy-mm-dd hh:mi') comment_time,
		co.member_id, mem.name from gibu_fund_comment co, member mem
		where co.member_id=mem.id order by comment_no desc
	)
) where page=#{commentPage} and fund_no=#{fundNo}

select
sum(amount) from donation_history where
to_char(donation_time,'yyyy')=#{value}

select
count(distinct(member_id)) from donation_history where
to_char(donation_time,'yyyy')=#{value}
