mapper.select:
		 select board_no, title, write_date, content, hits from(
  		select board_no, title, write_date, content, hits, ceil(rownum/5) as page from
		(select board_no, title, to_char(write_date,'YYYY.MM.DD') as write_date, content, hits from free_board order by board_no desc))where page=#{value}

select page from(
	select board_no, ceil(rownum/5) as page from(
		select board_no from free_board order by board_no desc
	)
) where board_no=#{boardNo}
